        await InsertProduct();
        ProductModel = new ProductModel(); // Reset EditForm Inputs
    }

    private async Task InsertProduct()
    {
        string sql = "insert into products (ProductNumber, Name, Description, Price, Quantity, ImagePath, InStock, DateAdded, Category, Seller)" +
                "values (@ProductNumber, @Name, @Description, @Price, @Quantity, @ImagePath, @InStock, @DateAdded, @Category, @Seller);";

        if (ProductModel.Quantity > 0)
        {
            ProductModel.InStock = true;
        }

        await _data.SaveData(sql, new
        {
            ProductNumber = ProductModel.ProductNumber,
            Name = ProductModel.Name,
            Description = ProductModel.Description,
            Price = ProductModel.Price,
            Quantity = ProductModel.Quantity,
            ImagePath = ProductModel.ImagePath,
            InStock = ProductModel.InStock,
            DateAdded = DateTime.Now,
            Category = ProductModel.Category,
            Seller = ProductModel.Seller,
        }
        , _config.GetConnectionString("default"));

        await OnInitializedAsync();
    }

