                        return await Task.FromResult(false);
                    }
                }
                else
                {
                    this.passwordErrMsg = "display: none;";
                    this.emailErrMsg = "display: initial;";

                    await DeleteUser(UserModel.Email);

                    return await Task.FromResult(false);
                }
            }
