                    if (UserModel.Password == passwordHash)
                    {
                        string name;

                        name = UserModel.FirstName + " " + UserModel.LastName;

                        var url = "/";

                        await AuthenticateUser(name, UserModel.Role, UserModel.Email, url);
                    }
                    else
